import { useCurrentFrame, interpolate, Easing } from "remotion";

export const TestAnimation = () => {
  const frame = useCurrentFrame();

  // Bouncing ball animation
  const bounce = interpolate(
    frame,
    [0, 15, 30, 45, 60, 75, 90, 105, 120],
    [0, 100, 0, 100, 0, 100, 0, 100, 0],
    {
      extrapolateRight: "clamp",
      easing: Easing.bezier(0.25, 0.1, 0.25, 1)
    }
  );

  // Rotation animation
  const rotation = interpolate(frame, [0, 120], [0, 360], {
    extrapolateRight: "clamp"
  });

  // Scale animation
  const scale = interpolate(
    frame,
    [0, 30, 60, 90, 120],
    [1, 1.2, 1, 1.2, 1],
    {
      extrapolateRight: "clamp"
    }
  );

  return (
    <div
      style={{
        flex: 1,
        backgroundColor: "#1a1a2e",
        justifyContent: "center",
        alignItems: "center",
        display: "flex",
        fontFamily: "Inter, -apple-system, BlinkMacSystemFont, sans-serif"
      }}
    >
      <div
        style={{
          width: 80,
          height: 80,
          backgroundColor: "#f39c12",
          borderRadius: "50%",
          transform: `translateY(${-bounce}px) rotate(${rotation}deg) scale(${scale})`,
          boxShadow: "0 10px 30px rgba(243, 156, 18, 0.5)"
        }}
      />
      <div
        style={{
          position: "absolute",
          bottom: 100,
          fontSize: 24,
          color: "#ecf0f1",
          fontWeight: "bold",
          textAlign: "center",
          fontFamily: "Inter, -apple-system, BlinkMacSystemFont, sans-serif"
        }}
      >
        Claude Code MCP Test Animation!
      </div>
    </div>
  );
};

export const duration = 4;
